import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';

const Cart = () => {

    const { cart, user } = useSelector((state) => ({ ...state }));
    const dispatch = useDispatch();

    const getTotal = () => {
        return cart.reduce((currentValue, nextValue) => {
            return currentValue + nextValue.count * nextValue.price
        }, 0);
    };

    const saveOrderToDb = () => {

    };

    return (
        <div className="container-fluid pt-2">

            <div className="row">
                <div className="col-md-8">
                    <h4>Cart / {cart.length} Product</h4>
                    {
                        !cart.length
                            ? (<p>No products in cart.<Link to="/shop"> Continue Shopping?</Link></p>)
                            : ("show cart items")
                    }
                </div>
                <div className="col-md-4"><h4>Order Summary</h4>
                    <hr />
                    <p>Products</p>
                    {cart.map((c, i) => (
                        <div key={i}>
                            <p>{c.title} x {c.count} = ${c.price * c.count}</p>
                        </div>
                    ))}

                    <hr />
                    Total: <b>${getTotal()}</b>
                    <hr />
                    {user
                        ? (<button 
                        onClick={saveOrderToDb} 
                        className="btn- btn-s btn-primary mt-2" 
                        disabled={!cart.length}
                        >
                        Proceed to Checkout
                        </button>)
                        : (<button className="btn- btn-s btn-primary mt-2">Login to Checkout</button>)
                    }
                </div>
            </div>
        </div>
    );
};

export default Cart;
