import React, { useState } from 'react';

import { Menu } from 'antd';
import {
    // HomeOutlined,
    LoginOutlined,
    UserOutlined,
    UserAddOutlined,
    LogoutOutlined,
    ShoppingOutlined,
} from '@ant-design/icons';
import { Link } from 'react-router-dom';
import firebase from 'firebase';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import Search from '../forms/Search';
import Yayas from './yayas.png';
import './header.css';

const { SubMenu, Item } = Menu; // Menu.subMenu



const Header = (props) => {

    /** Here the code checks what the theme is currently set to. 
     * If it is light, it will go dark, and vice versa. 
    */
   function changeTheme() {
    if (props.theme === 'light') {
        props.setTheme('dark');
    } else {
        props.setTheme('light');
    }
};
/* Here is an icon that is being passed to the Toggle in the return function below. 
So, if the theme is set to light, the icon will become HiMoon when the icon is toggled. 
If it is dark, the icon will turn into CgSun when the icon is toggled.*/
const icon = props.theme === 'light' ? <HiMoon size={40} /> : <CgSun size={40} />;

    const [current, setCurrent] = useState('home');
    let dispatch = useDispatch();
    let { user } = useSelector((state) => ({ ...state }));
    let history = useHistory();

    const handleClick = (e) => {
        // console.log(e.key);
        setCurrent(e.key);
    };

    const logout = () => {
        firebase.auth().signOut();
        dispatch({
            type: "LOGOUT",
            payload: null,
        });
        history.push("/login");
    };

    return (
        <Menu
            onClick={handleClick}
            selectedKeys={[current]}
            mode="horizontal"
            className="text-white"
            id="header-1"
        >
            <Item key="home" >
                <Link to="/"><img
                    src={Yayas}
                    className="home__image"
                    alt=""
                /></Link>
            </Item>

            <Item key="shop" icon={<ShoppingOutlined />} >
                <Link to="/shop">Shop</Link>
            </Item>

            <Item key="light-dark-mode" >
            <Toggle onClick={changeTheme}>
                    {icon}
                </Toggle>
            </Item>

            {!user && (
                <Item
                    key="register"
                    icon={<UserAddOutlined />}
                    className="float-right"
                >
                    <Link to="/register">Register</Link>
                </Item>
            )}

            {!user && (
                <Item key="login" icon={<LoginOutlined />} className="float-right">
                    <Link to="/login">Login</Link>
                </Item>
            )}

            {user && (
                <SubMenu
                    icon={<UserOutlined />}
                    title={user.email && user.email.split('@')[0]}
                    className="float-right"
                >

                    {
                        user && user.role === 'subscriber' &&
                        <Item key="setting:1">
                            <Link to="/user/history">
                                Dashboard
                                </Link>
                        </Item>
                    }

                    {
                        user && user.role === 'admin' &&
                        <Item key="setting:1">
                            <Link to="/admin/dashboard">
                                Dashboard
                                </Link>
                        </Item>
                    }



                    <Item icon={<LogoutOutlined />} onClick={logout}>
                        Logout
                    </Item>
                </SubMenu>
            )}

            <span className="float-right p-1">
                <Search />
            </span>

        </Menu>
    )
};
export default Header;
