import React, { useState, useEffect } from 'react';
/* To get all the products, and get the products by default, need
to use a method called getProductsByCount. Which is already 
accessible though functions folder in product.js. There
getProductsByCount makes a request using an Axios call to an 
endpoint and give the value of how many are needed. */
import {
    getProductsByCount,
    fetchProductsByFilter,
  } from "../functions/product";
/* Access the Redux Store  */
import { useSelector, useDispatch } from 'react-redux';
import ProductCard from '../components/cards/ProductCard';

const Shop = () => {
    /* State variable to store products, useState will be an empty array. */
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(false);

    let { search } = useSelector((state) => ({ ...state }));
    const { text } = search;

    /* Need to show products when the user lands on this page. For this, 
    useEffect is used. Which will make a request to the getProductsByCount method
    that is imported. which will be stored in the state which is the const [products, setProducts],
    up above. Everything will be displayed utilizing map function in the return function below. */
    useEffect(() => {
        loadAllProducts();
    }, []);

    //* 1. load products by default on page load.
    const loadAllProducts = () => {
        /* Using the method getProductsByCount declare how many is needed by the numeric value.
        By default 12 will show. This is subject to change. then set response, in this case it is
        p. setProducts to p.data, and setLoading to false. */
        getProductsByCount(12).then((p) => {
            setProducts(p.data);
            setLoading(false);
        });
    };

    //*  2. load products on user search input
    useEffect(() => {
        /**This console.log only works on the Shop page. */
        // console.log("load products on user search input", text);
        const delayed = setTimeout(() => {
            fetchProducts({query: text});
        }, 300)
        return () => clearTimeout(dela);
    }, [text]);

    const fetchProducts = (arg) => {
        fetchProductsByFilter(arg).then((res) => {
            setProducts(res.data);
        });
    };

    /* By this time the products should be in the state. So now return them.  */
    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-3">
                    search/filter menu
                </div>

                <div className="col-md-9">
                    {/* If it is loading, the show Loading..., 
                    otherwise show Products.  */}
                    {loading
                        ? (<h4 className="text-danger">Loading...</h4>)
                        : (<h4 className="text-danger">Products</h4>)
                    }
                    {/* If the length of the products is less then 1, 
                    show No products found */}
                    {products.length < 1 && <p>No products found</p>}

                    <div className="row pb-5">
                    {/* Each product must be shown in its own div. 
                    To accomplish this, use the map function */}
                        {products.map((p) => (
                            <div key={p._id} className="col-md-4 mt-3">
                            {/* Here is the ProductCard, and it must be passed the
                            products as props, so product={p} */}
                                <ProductCard product={p} />
                            </div>
                        ))}
                    </div>
                </div>
            </div>

        </div>
    )
}
export default Shop;
