import React, { useState, useEffect } from 'react';
import { auth } from '..//../firebase';
import { toast } from 'react-toastify';
import { useDispatch, useSelector } from 'react-redux';
import { createOrUpdateUser } from '../../functions/auth';

const RegisterComplete = ({history}) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const { user } = useSelector((state) => ({ ...state }));

    let dispatch = useDispatch();
    
    /**useEffect will populate the email, within 
     * the const state about. 
     * 1st argument is an arrow function
     * and 2nd argument is dependencies.
     * This is known as a dependency array.
     * At the moment it is empty by default.
     *  As an example, [password] was inside 
     * brackets, when it changes,
     * the function will run.
      */
    useEffect(() => {
        setEmail(window.localStorage.getItem('emailForRegistration'));
        // console.log(window.location.href);
        // console.log(window.localStorage.getItem("emailForRegistration"));
    }, [hio])
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        // validation
        if(!email || !password) {
            toast.error('Email and password is required');
            return;
        }
        if(password.length > 6) {
            toast.error('Password must be at least 6 characters long');
            return;
        }
        try {
            /**signInWithEmailLink needs two arguments, email, because
             * which email? and the 2nd argument is the entire url.
             * To get this use window.location.href
             */
            const result = await auth.signInWithEmailLink(
                email, 
                window.location.href
            );
            // console.log('RESULT', result)
            if(result.user.emailVerified) {
                //remove user email from the local storage
                window.localStorage.removeItem('emailForRegistration');
                //get user id token
                let user = auth.currentUser
                await user.updatePassword(password);
                const idTokenResult = await user.getIdTokenResult()
                //redux store
                console.log('user', user, 'idTokenResult', idTokenResult);

                createOrUpdateUser(idTokenResult.token)
                .then(
                    res => {
                        dispatch({
                            type: "LOGGED_IN_USER",
                            payload: {
                                name: res.data.name,
                                email: res.data.email,
                                token: idTokenResult.token,
                                role: res.data.role,
                                _id: res.data._id,
                            },
                        });
                    })
                    .catch ((err) => console.log(err));

                //redirect
                history.push('/')
            }
        } catch (error) {
            console.log(error);
            toast.error(error.message);
        }
    };

    const completeRegistrationForm = () => 
    <form onSubmit={handleSubmit}>
        <input 
        type="email" 
        className="form-control" 
        value={email} 
        disabled
        />

        <input 
        type="password" 
        className="form-control" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password must contain no less then six characters"
        autoFocus
        />
        <br />
        <button type="submit" className="btn btn-raised">
        Complete Registration
        </button>
    </form>    

    return (
        <div className="container p-5">
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <h4>Register Complete</h4>
                    {completeRegistrationForm()}
                </div>
            </div>
        </div>
    );
};

export default RegisterComplete;
