import firebase from "firebase/app";
import "firebase/auth";
// firebase config
const config = {
  apiKey: "AIzaSyCh1NVMyWCO0a7eGXNgZvdlYI9bs7KSTbg",
  authDomain: "yayas-island-mart.firebaseapp.com",
  projectId: "yayas-island-mart",
  storageBucket: "yayas-island-mart.appspot.com",
  messagingSenderId: "78241790177",
  appId: "1:78241790177:web:8c4e3ac59d147f87da5fa0"
  };
  // initialize firebase app
if (!firebase.apps.length) {
  firebase.initializeApp(config);
}

  // export
// export default firebase;
export const auth = firebase.auth();
export const googleAuthProvider = new firebase.auth.GoogleAuthProvider();
