    }

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <AdminNav />
                </div>

                <div className="col-md-10">
                    <h4>Product update</h4>
                    {JSON.stringify(slug)}
                    <hr />
                </div>

            </div>
        </div>
    );

};

export default ProductUpdate;
