import React from 'react'
import Jumbotron from '../components/cards/Jumbotron';
import LoadingCard from '../components/cards/LoadingCard';
import ProductCard from '../components/cards/ProductCard';
import { getProducts } from '../functions/product';
import {NewArrivals} from '../components/home/NewArrivals';
import './pages.css';

const Home = () => {

    return (
        <>
            <div className="jumbotron text-primary h1 font-weight-bold text-center font-italic">
                {/* {loading ? (<h4>Loading...</h4>) : (<h4>All Products</h4>)} */}
                <Jumbotron text={[
                    'welcome to yayas island mart',
                    'help support small minority own businesses',
                    'consider buying today',
                    'from the bottom of our hearts',
                    'thank you',
                ]} />
            </div>

            <h4 className="text-center p-3 mt-5 mb-5 display-4 new-arrivals-jumbotron">
                New Arrivals
            </h4>

        </>
    )
}

export default Home;
