import React from 'react';
import {
    Document,
    Page,
    Text,
    StyleSheet
} from '@react-pdf/renderer';
import {Table, TableHeader, TableCell, TableBody, DataTableCell} from '@david.kucsai/react-pdf-table';

const Invoice = ({order}) => (
    <Document>
        <Page style={styles.body}>
            <Text style={styles.hd}></Text>
            <Text>Order Invoice</Text>
        </Page>
    </Document>
);

    // Create styles
    const styles = StyleSheet.create({
        // page: {
        //     flexDirection: 'row',
        //     backgroundColor: '#E4E4E4'
        // },
        // section: {
        //     margin: 10,
        //     padding: 10,
        //     flexGrow: 1
        // },
        body: {
            paddingTop: 35,
            paddingBottom: 65,
            paddingHorizontal: 35,
        },
        title: {
            fontSize: 24,
            textAlign: "center",
        },
        author: {
            fontSize: 12,
            textAlign: "center",
            marginBottom: 40,
        },
        subtitle: {
            fontSize: 18,
            margin: 12,
        },
        text: {
            margin: 12,
            fontSize: 14,
            textAlign: "justify",
        },
        image: {
            marginVertical: 15,
            marginHorizontal: 100,
        },
        header: {
            fontSize: 12,
            marginBottom: 20,
            textAlign: "center",
            color: "grey",
        },
        footer: {
            padding: "100px",
            fontSize: 12,
            marginBottom: 20,
            textAlign: "center",
            color: "grey",
        },
        pageNumber: {
            position: "absolute",
            fontSize: 12,
            bottom: 30,
            left: 0,
            right: 0,
            textAlign: "center",
            color: "grey",
        },
    });

export default Invoice;
