import React, { useState, useEffect } from 'react';
import AdminNav from '../../components/nav/AdminNav';
import { getOrders, changeStatus } from '../../functions/admin'; 
import { getSaveAddress } from '../../functions/user'; //! Trying to make getSavedUserAddress work , 

import { useSelector, useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import Orders from '../../components/order/Orders';
const AdminDashboard = () => {
    const [orders, setOrders] = useState([]);
    // const { user, setUsers } = useState([]); //! Trying to make getSaveUserAddress work
    const { user, setUser } = useSelector((state) => ({ ...state }));

    useEffect(() => {
        loadOrders();
        loadUser(); //! Trying to make getSaveUserAddress work
    }, []);

    const loadOrders = () =>
        getOrders(user.token).then((res) => {
            console.log(JSON.stringify(res.data, null, 4));
            setOrders(res.data);
        });

    const loadUser = () =>
        getSaveAddress(user.toke).then((res) => {
            console.log(JSON.stringify(res.data, null, 4));
            // return;
            setUser(res.data);
        }); //! Trying to make getSaveUserAddress work

    const handleStatusChange = (orderId, orderStatus) => {
        changeStatus(orderId, orderStatus, user.token)
            .then((res) => {
                toast.success('Status updated');
                loadOrders();
                loadUser();
            });
    };

    return (
        <div>
            <div className='container-fluid'>
                <div className="row">

                    <div className="col-md-2">
                        <AdminNav />
                    </div>

                    <div className="col-md-10" >
                        <h4>Admin Dashboard</h4>
                        {/* {JSON.stringify(orders)} */}
                        <Orders orders={orders} handleStatusChange={handleStatusChange} />
                    </div> 
                    
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
