import React from 'react';
/* The useHistory hook is being imported to redirect the user to shop page */
import { useHistory } from 'react-router-dom';
/* Need access to the Redux Store, use  useSelector, and useDispatch to 
dispatch the action with the action type and the action payload to the 
window store.*/
import { useSelector, useDispatch } from 'react-redux';
import { SearchOutlined } from '@ant-design/icons';

const Search = () => {
    /* To access Redux, use the variable dispatch to access Redux Store using useDispatch hook */
    let dispatch = useDispatch();
    const { search } = useSelector((state) => ({ ...state }));
    const { text } = search;

    const history = useHistory();

    const handleChange = (e) => {
        dispatch({
            type: "SEARCH_QUERY",
            payload: { text: e.target.value },
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        history.push(`/shop?${text}`);
    };

    return (
        <form className="form-inline my-2 my-lg-0" onSubmit={handleSubmit}>
            <input
                onChange={handleChange}
                type='search'
                value={text}
                className="form-control mr-sm-2 text-white"
                placeholder="Search"
            />
            <SearchOutlined onClick={handleSubmit} style={{ cursor: 'pointer' }} />
        </form>
    )
}

export default Search;
