import React from 'react';
import { Card, Tabs } from 'antd';
import { Link } from 'react-router-dom';
import { HeartOutlined, ShoppingCartOutlined } from '@ant-design/icons';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import './cards.css';
import Yayas from '../../images/yayas.png';
import ProductListItems from './ProductListItems';
import StarRating from 'react-star-ratings';
import RatingModal from '../modal/RatingModal';

const { TabPane } = Tabs;

// This is children component of Product page
const SingleProduct = ({ product, onStarClick, star }) => {
    const { title, images, description, _id } = product;

    return (
        <>

            <div className="col-md-7">
                {images && images.length
                    ? (<Carousel showArrows={true} autoPlay infiniteLoop>
                        {images && images.map((i) => <img src={i.url} key={i.public_id} />)}
                    </Carousel>)
                    : (<Card cover={<img src={Yayas} className="mb-3 card-image" />}></Card>
                    )}

                <Tabs type="card">
                    <TabPane tab="Description" key="1">
                        {description && description}
                    </TabPane>
                    <TabPane tab="Learn More" key="2">
                        Please feel free to email to learn more about the product,
                        or visit to our Question and Answer Page
                        </TabPane>
                </Tabs>
            </div>

            <div className="col-md-5">
                <h1 className="single-card-title p-3">{title}</h1>


                {product && product.ratings &&  product.ratings.length}

                <Card
                    actions={[
                        <>
                            <ShoppingCartOutlined className="text-success" />
                            <br />
                            Add to Cart
                        </>,
                        <Link to="/">
                            <HeartOutlined className="text-info" />
                            <br />
                            Add to Wishlist
                        </Link>,
                        <RatingModal>
                            <StarRating
                                name={_id}
                                numberOfStars={5}
                                rating={star}
                                changeRating={onStarClick}
                                isSelectable={true}
                                isAggregateRating={true}
                                starRatedColor="rgb(255, 196, 13)"
                                starEmptyColor="rgb(203, 211, 227)"
                                starHoverColor="rgb(245, 224, 81)"
                            />
                        </RatingModal>,
                    ]}
                >
                    <ProductListItems product={product} />
                </Card>
            </div>

        </>
    );
};

export default SingleProduct;
