import React from 'react';



const ShowPaymentInfo = ({ order }) =>
(
    <div>
        <p>
            <span>Order Id: {order.paymentIntent.id}</span>{''}
            <span>Amount: {(order.paymentIntent.amount /= 100).toLocaleString('en-US')}</span>
        </p>
    </div>
)

export default ShowPaymentInfo;
