import { combineReducers } from 'redux';
import { userReducer } from './userReducer';
import { searchReducer } from './searchReducer';

const rootReducer = combineReducers({
    /* user and search properties are now in the redux store */
    user: userReducer,
    search: searchReducer,

}); 

export default rootReducer; 

/* 
Store - holds the code's state - There is only one state.
Action - State can be modified using actions - simple objects.
Dispatcher- Actions needs to be sent be someone - known as dispatching
an action.
Reducer - receives the action and modifies the state to give us a new 
state.
-pure functions
-only mandatory argument is the 'type 
Subscriber - listens for state change to update*/
