import React, { useState, useEffect } from 'react';
/* To get all the products, and get the products by default, need
to use a method called getProductsByCount. Which is already 
accessible though functions folder in product.js. There
getProductsByCount makes a request using an Axios call to an 
endpoint and the count is e*/
import { getProductsByCount } from '../functions/product';
import { useSelector, useDispatch } from 'react-redux';
import ProductCard from '../components/cards/ProductCard';

const Shop = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        loadAllProducts();
    }, []);

    const loadAllProducts = () => {
        getProductsByCount(12).then((p) => {
            setProducts(p.data);
            setLoading(false);
        });
    };

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-3">
                    search/filter menu
            </div>

                <div className="col-md-9">
                    {loading
                        ? (<h4 className="text-danger">Loading...</h4>)
                        : (<h4 className="text-danger">Products</h4>)
                    }

                    {products.length < 1 && <p>No products found</p>}

                    <div className="row pb-5">
                        {products.map((p) => (
                            <div key={p._id} className="col-md-4 mt-3">
                                <ProductCard product={p} />
                            </div>
                        ))}
                    </div>
                </div>
            </div>

        </div>
    )
}
export default Shop;
