import React, { useState, useEffect } from 'react';
import AdminNav from '../../components/nav/AdminNav';
import { getOrders, changeStatus, getSavedUserAddress } from '../../functions/admin'; //! Trying to make getSavedUserAddress work
import { useSelector, useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import Orders from '../../components/order/Orders';
import OrderUserInfo from '../../components/order/OrderUserInfo';//! Trying to make getAddress work
const AdminDashboard = () => {
    const [orders, setOrders] = useState([]);
    const { users, setUser } = useSelector((state) => ({ ...state }));

    useEffect(() => {
        loadOrders();
    }, []);

    const loadOrders = () =>
        getOrders(user.token).then((res) => {
            console.log(JSON.stringify(res.data, null, 4));
            setOrders(res.data);
        });

    const loadUserAddress = () =>
        getSavedUserAddress(user.token).then((res) => {
            console.log(JSON.stringify(res.data, null, 4));
            setUser(res.data);
        }); //! Trying to make getSavedUserAddress work

    const handleStatusChange = (orderId, orderStatus) => {
        changeStatus(orderId, orderStatus, user.token)
            .then((res) => {
                toast.success('Status updated');
                loadOrders();
            });
    };

    return (
        <div>
            <div className='container-fluid'>
                <div className="row">

                    <div className="col-md-2">
                        <AdminNav />
                    </div>

                    <div className="col-md-10" >
                        <h4>Admin Dashboard</h4>
                        {/* {JSON.stringify(orders)} */}
                        <Orders orders={orders} handleStatusChange={handleStatusChange} />
                        <OrderUserInfo users={users} /> //! Trying to make getAddress work
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
