import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useSelector, useDispatch } from 'react-redux';
import { createPaymentIntent } from '../functions/stripe';
import { Link } from 'react-router-dom';
import "./stripeCheckout.css";

const StripeCheckout = ({ history }) => {

    const dispatch = useDispatch();
    const { user, coupon } = useSelector((state) => ({ ...state }));

    const [succeeded, setSucceeded] = useState(false);
    const [error, setError] = useState(null);
    const [processing, setProcessing] = useState('');
    const [disabled, setDisabled] = useState(true);
    const [clientSecret, setClientSecret] = useState('');

    const [cartTotal, setCartTotal] = useState(0);
    const [totalAfterDiscount, setTotalAfterDiscount] = useState(0);
    const [payable, setPayable] = useState(0);

    const stripe = useStripe();
    const elements = useElements();

    useEffect(() => {
        createPaymentIntent(user.token, coupon)
            .then((res) => {
                console.log('create payment intent', res.data);
                setClientSecret(res.data.clientSecret);
                // additional response received on successful payment
                setCartTotal(res.data.cartTotal);
                set
            });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setProcessing(true);

        const payload = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement),
                billing_details: {
                    name: e.target.name.value,
                },
            },
        });
        if (payload.error) {
            setError(`Payment failed ${payload.error.message}`)
            setProcessing(false)
        } else {
            // here you get result after successful payment
            // create order and save in database for admin to process
            // empty user cart from redux store and local storage
            console.log(JSON.stringify(payload, null, 4));
            setError(null);
            setProcessing(false);
            setSucceeded(true);

        }
    };

    const handleChange = async (e) => {
        // listen to changes in the card element
        // and display any errors as the 
        // customer types their card details
        setDisabled(e.empty); // disable pay button if errors
        setError(e.error ? e.error.message : ""); // show error message
    };

    const cartStyle = {
        style: {
            base: {
                color: "#32325d",
                fontFamily: "Arial, sans-serif",
                fontSmoothing: "antialiased",
                fontSize: "16px",
                "::placeholder": {
                    color: "#32325d",
                },
            },
            invalid: {
                color: "#fa755a",
                iconColor: "#fa755a",
            },
        },
    };

    return (
        <>
            <div className={succeeded ? 'result message' : 'result-message hidden'} >
                <b>Payment Successful...</b> 
                <Link to='/user/history' id='card-payment'>
                    View in your purchase history
                </Link>
            </div>
            <form
                id="payment-form"
                className="stripe-form"
                onSubmit={handleSubmit}
            >
                <CardElement
                    id="card-element"
                    options={cartStyle}
                    onChange={handleChange}
                />
                <button
                    className="stipe-button"
                    disabled={processing || disabled || succeeded}
                >
                    <span id="button-text">
                        {processing
                            ? (<div className="spinner" id="spinner"></div>)
                            : ("Pay")
                        }
                    </span>
                </button>
                <br />
                {error && (
                    <div className="card-error" role="alert">
                        {error}
                    </div>
                )
                }
            </form>



        </>
    )
};

export default StripeCheckout;
