import React, { useState, useEffect } from 'react';
import AdminNav from '../../../components/nav/AdminNav';
import { toast } from 'react-toastify';
import { useSelector } from 'react-redux';
import { getCategories } from '../../../functions/category';
import {
    createSub,
    getSub,
    getSubs,    
    removeSub, 
} from '../../../functions/sub';
import { Link } from 'react-router-dom';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';
import CategoryForm from '../../../components/forms/CategoryForm';
import LocalSearch from '../../../components/forms/LocalSearch';

const SubCreate = () => {

    const { user } = useSelector(state => ({ ...state }));
    const [name, setName] = useState('');
    const [loading, setLoading] = useState(false);
    // for list categories that is shown in the select option
    const [categories, setCategories] = useState([]);
    /* This is for the category the user clicked.
        Goes to the sub Category that was created 
        in the backend. 
        This will be sent as parent.*/
    const [category, setCategory] = useState("");

    //searching and filtering
    //step 1
    const [keyword, setKeyword] = useState("");

    useEffect(() => {
        loadCategories();
    }, []);

    const loadCategories = () =>
        getCategories().then((c) => setCategories(c.data));

    const loadSubs = () =>
        getCategories().then((c) => setSubs(c.data));

    const handleSubmit = (e) => {
        e.preventDefault();
        // console.log(name);
        setLoading(true);
        createSub({ name, parent: category }, user.token)
            .then((res) => {
                // console.log(res)
                setLoading(false);
                setName('');
                toast.success(`"${res.data.name}" is created`);
            })
            .catch((err) => {
                console.log(err);
                setLoading(false);
                if (err.response.status === 400) toast.error(err.response.data);
            });
    };

    const handleRemove = async (slug) => {
        // let answer = window.confirm("Delete?");
        // console.log(answer, slug);
        if (window.confirm("Delete?")) {
            setLoading(true);
            removeSub(slug, user.token)
                .then(res => {
                    setLoading(false);
                    toast.error(`${res.data.name} deleted`);
                })
                .catch(err => {
                    setLoading(false);
                    if (err.response.status === 400) toast.error(err.response.data);
                });
        }
    };

    // step 4
    const searched = (keyword) => (c) => c.name.toLowerCase().includes(keyword);

    return (
        <div className='container-fluid'>
            <div className="row">
                <div className="col-md-2">
                    <AdminNav />
                </div>
                <div className="col">
                    {loading
                        ? (<h4 className="text-danger">Loading...</h4>)
                        : (<h4>Create sub category</h4>)
                    }

                    <div className="form-group">
                        <label>Parent Category</label>
                        <select 
                        name="category" 
                        className="form-control"
                        onChange={(e) => setCategory(e.target.value)}
                        >

                        <option>Please select</option>

                            {
                                categories.length > 0 &&
                                categories.map((c) => ( 
                                    <option key={c._id} value={c._id}>
                                        {c.name}
                                    </option>
                                ))
                            }
                        </select>
                    </div>
                    {JSON.stringify(category)}
                    <CategoryForm
                        handleSubmit={handleSubmit}
                        name={name}
                        setName={setName}
                    />

                    {/** step 2 and step 3 */}
                    <LocalSearch
                        keyword={keyword}
                        setKeyword={setKeyword}
                    />

                    {/* {categories.length} */}
                   
                    {/** step 5 */}
                    {/* {categories.filter(searched(keyword)).map((c) => (
                        <div className="alert alert-primary" key={c._id}>
                            {c.name}
                            <span
                                onClick={() => handleRemove(c.slug)}
                                className="btn btn-sm float-right"
                            >
                                <DeleteOutlined className="text-danger" />
                            </span>
                            <Link to={`/admin/category/${c.slug}`}>
                                <span className="btn btn-sm float-right">
                                    <EditOutlined className="text-warning" />
                                </span>
                            </Link>
                        </div>
                    ))} */}

                </div>
            </div>
        </div>
    );
};

export default SubCreate;
