import React, { useState, useEffect } from 'react';
import AdminNav from '../../components/nav/AdminNav';
import { getOrders, changeStatus } from '../../functions/admin'; 
import { getSavedUserAddress } from '../../functions/user'; //! Trying to make getSavedUserAddress work , 

import { useSelector, useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import Orders from '../../components/order/Orders';
const AdminDashboard = () => {
    const [orders, setOrders] = useState([]);
    // const { users, setUsers } = useState([]);
    const { user } = useSelector((state) => ({ ...state }));

    useEffect(() => {
        loadOrders();
        loadUser();
    }, []);

    const loadOrders = () =>
        getOrders(user.token).then((res) => {
            console.log(JSON.stringify(res.data, null, 4));
            setOrders(res.data);
        });

    // const loadUserAddress = () =>
    //     getSavedUserAddress(user.token).then((res) => {
    //         console.log(JSON.stringify(res.data, null, 4));
    //         setUsers(res.data);
    //     }); //! Trying to make getSavedUserAddress work

    const handleStatusChange = (orderId, orderStatus) => {
        changeStatus(orderId, orderStatus, user.token)
            .then((res) => {
                toast.success('Status updated');
                loadOrders();
            });
    };

    return (
        <div>
            <div className='container-fluid'>
                <div className="row">

                    <div className="col-md-2">
                        <AdminNav />
                    </div>

                    <div className="col-md-10" >
                        <h4>Admin Dashboard</h4>
                        {/* {JSON.stringify(orders)} */}
                        <Orders orders={orders} handleStatusChange={handleStatusChange} />
                    </div> 
                    
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
