import React from 'react';
import Jumbotron from '../components/cards/Jumbotron';
import CategoryList from '../components/category/CategoryList';
import SubList from '../components/sub/SubList';
import BestSellers from '../components/home/BestSellers';
import NewArrivals from '../components/home/NewArrivals';
import './pages.css';

const Home = () => {

    return (
        <>
            <div className="jumbotron text-primary h1 font-weight-bold text-center font-italic">
                {/* {loading ? (<h4>Loading...</h4>) : (<h4>All Products</h4>)} */}
                <Jumbotron text={[
                    'welcome to yayas island mart',
                    'from the bottom of our hearts',
                    'thank you',
                ]} />
            </div>

            

            <h4 className="text-center text-white p-3 mt-5 mb-5 display-4 new-arrivals-jumbotron">
                Buyers Love These Products
            </h4>
            <BestSellers />
            <h4 className="text-center text-white p-3 mt-5 mb-5 display-4 new-arrivals-jumbotron">
                Just In
            </h4>
            <NewArrivals />
            <h4 className="text-center text-white p-3 mt-5 mb-5 display-4 new-arrivals-jumbotron">
                Categories
            </h4>
            <CategoryList />
            <h4 className="text-center text-white p-3 mt-5 mb-5 display-4 new-arrivals-jumbotron">
                Sub Categories
            </h4>
            <SubList />

            <br />
            <br />

        </>
    )
}

export default Home;
