import React, { useState, useEffect } from 'react';
import { auth } from '..//../firebase';
import { toast } from 'react-toastify';

const RegisterComplete = ({history}) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    /**useEffect will populate the email, within 
     * the const state about. 
     * 1st argument is an arrow function
     * and 2nd argument is dependencies.
     * This is known as a dependecy array.
     * At the moment it is empty by default.
     *  As an example, [password] was inside 
     * brackets, when it changes,
     * the function will run.
      */
    useEffect(() => {
        setEmail(window.localStorage.getItem('emailForRegistration'));
        console.log(window.location.href);
        console.log(window.localStorage.getItem("emailForRegistration"));
    }, [])
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            /**signInWithEmailLink needs two arguments, email, because
             * which email? and the 2nd arguement is the entire url.
             * To get this use window.location.href
             */
            const result = await auth.signInWithEmailLink(
                email, 
                window.location.href
            );
        } catch (error) {

        }
    };

    const completeRegistrationForm = () => 
    <form onSubmit={handleSubmit}>
        <input 
        type="email" 
        className="form-control" 
        value={email} 
        disabled
        />

        <input 
        type="password" 
        className="form-control" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Enter Password"
        autoFocus
        />
        <br />
        <button type="submit" className="btn btn-raised">
        Complete Registration
        </button>
    </form>    

    return (
        <div className="container p-5">
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <h4>Register Complete</h4>
                    {completeRegistrationForm()}
                </div>
            </div>
        </div>
    );
};

export default RegisterComplete;
